def get_sparse_graph(node_groups, node_in_group_ids, n_node):
    """

    :param node_groups: list({group_id_0:[node0,node1,...], ...})
    :param node_in_group_ids: list({node0:group_id, ...})
    :param n_node: int
    :return:
    """
    row = []
    col = []

    for node_group, node_in_group_id in zip(node_groups, node_in_group_ids):
        row = row + [i1 for i1 in range(n_node) for _ in range(len(node_group[node_in_group_id[i1]]))]
        col = col + [i2 for i1 in range(n_node) for i2 in node_group[node_in_group_id[i1]]]
    value = np.ones_like(row)
    graph = csr_matrix((value, (row, col)), shape=(n_node, n_node))

    print(graph)
    return graph


